import React, { useState } from 'react';
import RankingBannerImage from '../Ranking-banner.jpg'; 

const AboutHeroSection = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <section className="about-hero-section">
            <div className="container">
                {/* Top Section */}
                <h1 className="about-hero-title">
                    INTI International University
                </h1>
                <p className="about-hero-description">
                    INTI International University, Nilai serves as the flagship campus that offers industry relevant programmes 
                    across various disciplines along with a residential campus experience.
                </p>

                {/* Image Section */}
                <div className="ranking-banner-container">
                    <img 
                        src={RankingBannerImage} 
                        alt="INTI University Rankings Banner" 
                        className="ranking-banner-image" 
                    />
                </div>

                {/* Menu Icon */}
                <div className="menu-icon" onClick={toggleMenu}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>

                {/* Slide Menu */}
                <div className={`side-menu ${isMenuOpen ? 'open' : ''}`}>
                    <ul>
                        <li>Home</li>
                        <li>Faculty Exchange</li>
                        <li>Study Tour</li>
                        <li>MOU/S</li>
                        <li>Dual Degree Program</li>
                        <li>Franchise Program</li>
                        <li>Mobility</li>
                        <li>Global Partners</li>
                    </ul>
                </div>
            </div>

            {/* Inline CSS */}
            <style>{`
                /* Side Menu */
                .side-menu {
                    position: fixed;
                    top: 0;
                    left: -280px;
                    width: 280px;
                    height: 100%;
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    box-shadow: 4px 0 20px rgba(0,0,0,0.3);
                    padding-top: 80px;
                    transition: left 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
                    z-index: 1000;
                    overflow-y: auto;
                }

                .side-menu.open {
                    left: 0;
                }

                .side-menu::before {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    height: 80px;
                    background: rgba(255, 255, 255, 0.1);
                    border-bottom: 1px solid rgba(255, 255, 255, 0.2);
                }

                .side-menu ul {
                    list-style: none;
                    padding: 20px 0;
                    margin: 0;
                    text-align: left;
                }

                .side-menu li {
                    padding: 18px 30px;
                    font-size: 1.05rem;
                    font-weight: 500;
                    color: white;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    border-left: 4px solid transparent;
                    position: relative;
                }

                .side-menu li::before {
                    content: '›';
                    position: absolute;
                    left: 15px;
                    opacity: 0;
                    transition: all 0.3s ease;
                    font-size: 1.3rem;
                    font-weight: bold;
                }

                .side-menu li:hover {
                    background: rgba(255, 255, 255, 0.15);
                    border-left-color: white;
                    padding-left: 35px;
                    transform: translateX(5px);
                }

                .side-menu li:hover::before {
                    opacity: 1;
                    left: 18px;
                }

                .side-menu li:active {
                    background: rgba(255, 255, 255, 0.25);
                }
                }
            `}</style>
        </section>
    );
};

export default AboutHeroSection;
