import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/layout/Header.jsx';
import Footer from './components/layout/Footer.jsx';
import HeroSection from './components/sections/HeroSection.jsx';
import ReachSection from './components/sections/ReachSection.jsx';
import DiscoverPathSection from './components/sections/DiscoverPathSection.jsx';
import AboutHeroSection from './components/sections/AboutHeroSection.jsx'; 
import ScopusSection from './components/sections/ScopusSection.jsx'; 
import MOUListPage from './components/pages/MOUListPage.jsx'; 
import INTILoginPage from './components/pages/Login_Page.jsx';
import FacultyExchange from './components/sections/faculty_exchange.jsx';

// Component for the content displayed on the Home Page
const HomePageContent = () => (
    <>
        <HeroSection />
        <AboutHeroSection />
        <ReachSection />
        <ScopusSection /> 
        <DiscoverPathSection />
    </>
);

// Protected Route Component
const ProtectedRoute = ({ isAuthenticated, children }) => {
  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(true); // make true for testing

  const handleLogin = () => setIsAuthenticated(true);
  const handleLogout = () => setIsAuthenticated(false);

  return (
<BrowserRouter>
  <div className="min-h-screen flex flex-col bg-white font-sans antialiased">
    {isAuthenticated && <Header onLogout={handleLogout} />}

    <div className="flex flex-1">
      {isAuthenticated && <Sidebar />}
      <main className="flex-1">
          <Routes>
  {/* Login Route - redirect to home if already authenticated */}
  <Route 
    path="/login" 
    element={
      isAuthenticated ? (
        <Navigate to="/" replace />
      ) : (
        <INTILoginPage onLogin={handleLogin} />
      )
    } 
  />

  {/* Protected routes layout â€” Header, Sidebar, Footer stay visible */}
  {isAuthenticated && (
    <>
      <Route 
        path="/" 
        element={
          <ProtectedRoute isAuthenticated={isAuthenticated}>
            <HomePageContent />
          </ProtectedRoute>
        } 
      />

      <Route 
        path="/mou-partners" 
        element={
          <ProtectedRoute isAuthenticated={isAuthenticated}>
            <MOUListPage />
          </ProtectedRoute>
        } 
      />

      <Route 
        path="/faculty-exchange" 
        element={
          <ProtectedRoute isAuthenticated={isAuthenticated}>
            <FacultyExchange />
          </ProtectedRoute>
        } 
      />
    </>
  )}

  {/* Catch all route - redirect to login */}
  <Route path="*" element={<Navigate to="/login" replace />} />
</Routes>
 </main>
    </div>

    {isAuthenticated && <Footer />}
  </div>
</BrowserRouter>
  );
}

export default App;
