import React, { useState } from 'react';
import RankingBannerImage from '../Ranking-banner.jpg'; 

const AboutHeroSection = () => {
    const [activeIndex, setActiveIndex] = useState(2); // Study Tour is active by default

    const menuItems = [
        { label: 'Home' },
        { label: 'Faculty Exchange' },
        { label: 'Study Tour' },
        { label: 'MOU/S' },
        { label: 'Mobility' },
        { label: 'Global Partners' }
    ];

    const handleMenuClick = (index) => {
        setActiveIndex(index);
        console.log(`Navigating to: ${menuItems[index].label}`);
        // Add your navigation logic here
    };

    const handleLogout = () => {
        console.log("Logging out...");
        // Add logout logic here (e.g., Firebase signOut())
    };

    return (
        <>
            <section className="about-hero-section">
                {/* Sidebar */}
                <div className="side-menu">
                    <ul>
                        {menuItems.map((item, index) => (
                            <li 
                                key={index} 
                                className={activeIndex === index ? 'active' : ''}
                                onClick={() => handleMenuClick(index)}
                            >
                                <div className="menu-icon-container">
                                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                        {index === 0 && <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" />}
                                        {index === 1 && <><rect x="2" y="7" width="20" height="14" rx="2" ry="2" /><path d="M16 21V5a2 2 0 0 0-2-2h-4a2 2 0 0 0-2 2v16" /></>}
                                        {index === 2 && <><path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z" /><polyline points="3.27 6.96 12 12.01 20.73 6.96" /><line x1="12" y1="22.08" x2="12" y2="12" /></>}
                                        {index === 3 && <><path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z" /><polyline points="14 2 14 8 20 8" /></>}
                                        {index === 4 && <circle cx="12" cy="12" r="10" />}
                                        {index === 5 && <><path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" /><circle cx="9" cy="7" r="4" /><path d="M23 21v-2a4 4 0 0 0-3-3.87" /><path d="M16 3.13a4 4 0 0 1 0 7.75" /></>}
                                    </svg>
                                </div>
                                <span className="menu-label">{item.label}</span>
                            </li>
                        ))}
                    </ul>

                    {/* Logout Button */}
                    <div className="logout-button" onClick={handleLogout}>
                        <div className="menu-icon-container">
                            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
                                <polyline points="16 17 21 12 16 7" />
                                <line x1="21" y1="12" x2="9" y2="12" />
                            </svg>
                        </div>
                        <span className="menu-label">Logout</span>
                    </div>
                </div>

                <div className="container">
                    <h1 className="about-hero-title">INTI International University</h1>
                    <p className="about-hero-description">
                        INTI International University, Nilai serves as the flagship campus that offers industry relevant programmes 
                        across various disciplines along with a residential campus experience.
                    </p>
                    <div className="ranking-banner-container">
                        <img src={RankingBannerImage} alt="INTI University Rankings Banner" className="ranking-banner-image" />
                    </div>
                </div>
            </section>

            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                /* SIDEBAR */
                .side-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 90px;
                    height: 100vh;
                    background: #ffffff;
                    border-right: 1px solid #e5e7eb;
                    z-index: 1000;
                    overflow-y: auto;
                    padding-top: 120px;
                }

                .side-menu ul {
                    list-style: none;
                    margin: 0;
                    padding: 0;
                }

                .side-menu li {
                    padding: 16px 8px;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                    gap: 6px;
                    font-size: 12px;
                    color: #6b7280;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    border-left: 3px solid transparent;
                    text-align: center;
                }

                .side-menu li:hover {
                    background: #f9fafb;
                    color: #374151;
                }

                .side-menu li.active {
                    background: #eff6ff;
                    color: #3b82f6;
                    border-left-color: #3b82f6;
                }

                .menu-icon-container {
                    width: 24px;
                    height: 24px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    flex-shrink: 0;
                }

                .menu-icon-container svg {
                    width: 20px;
                    height: 20px;
                }

                .menu-label {
                    font-weight: 500;
                    white-space: nowrap;
                }

                /* LOGOUT BUTTON */
                .logout-button {
                    position: absolute;
                    bottom: 20px;
                    left: 0;
                    width: 100%;
                    padding: 16px 8px;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                    gap: 6px;
                    font-size: 12px;
                    color: #ef4444;
                    cursor: pointer;
                    border-top: 1px solid #e5e7eb;
                    transition: all 0.2s ease;
                }

                .logout-button:hover {
                    background: #fef2f2;
                    color: #dc2626;
                }

                /* MAIN CONTENT */
                .about-hero-section {
                    min-height: 100vh;
                    background: #ffffff;
                }

                .about-hero-section .container {
                    margin-left: 90px;
                    padding: 60px 40px;
                    max-width: 1200px;
                }

                .about-hero-title {
                    font-size: 2.5rem;
                    font-weight: 700;
                    color: #1f2937;
                    margin-bottom: 20px;
                }

                .about-hero-description {
                    font-size: 1.1rem;
                    color: #6b7280;
                    line-height: 1.7;
                    margin-bottom: 40px;
                    max-width: 800px;
                }

                .ranking-banner-container {
                    width: 100%;
                    margin-top: 40px;
                }

                .ranking-banner-image {
                    width: 100%;
                    height: auto;
                    border-radius: 8px;
                    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
                }

                /* RESPONSIVE */
                @media (max-width: 768px) {
                    .side-menu {
                        width: 70px;
                    }

                    .menu-label {
                        display: none;
                    }

                    .side-menu li {
                        justify-content: center;
                        padding: 16px 10px;
                    }

                    .logout-button {
                        padding: 16px 10px;
                    }

                    .about-hero-section .container {
                        margin-left: 70px;
                        padding: 40px 20px;
                    }

                    .about-hero-title {
                        font-size: 2rem;
                    }

                    .about-hero-description {
                        font-size: 1rem;
                    }
                }
            `}</style>
        </>
    );
};

export default AboutHeroSection;
