import React, { useState } from 'react';
import { Search, Mail, Phone, BookOpen, Globe, Plus, X, Camera } from 'lucide-react';

const FacultyExchange = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('All');
  const [isAddFormOpen, setIsAddFormOpen] = useState(false);
  const [isEditFormOpen, setIsEditFormOpen] = useState(false);
  const [editingTeacher, setEditingTeacher] = useState(null);
  const [newTeacher, setNewTeacher] = useState({
    name: '',
    department: '',
    position: '',
    specialization: '',
    email: '',
    phone: '',
    image: '',
    exchangeProgram: '',
    duration: '',
    publications: '',
    experience: ''
  });

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e0f2fe 0%, #ffffff 50%, #fae8ff 100%)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, sans-serif'
    },
    header: {
      background: 'linear-gradient(90deg, #2563eb 0%, #9333ea 100%)',
      color: 'white',
      padding: '4rem 1.5rem',
      textAlign: 'center',
      position: 'relative'
    },
    addButton: {
      position: 'absolute',
      top: '2rem',
      right: '2rem',
      background: 'rgba(255, 255, 255, 0.2)',
      border: '2px solid white',
      color: 'white',
      padding: '0.75rem 1.5rem',
      borderRadius: '2rem',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s',
      backdropFilter: 'blur(10px)'
    },
    headerTitle: {
      fontSize: '3rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
      margin: 0
    },
    headerSubtitle: {
      fontSize: '1.25rem',
      color: '#bfdbfe',
      margin: 0
    },
    contentContainer: {
      maxWidth: '1280px',
      margin: '-2rem auto 0',
      padding: '0 1.5rem 3rem'
    },
    searchSection: {
      background: 'white',
      borderRadius: '1rem',
      boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
      padding: '1.5rem',
      marginBottom: '2rem'
    },
    searchControls: {
      display: 'flex',
      gap: '1rem',
      flexWrap: 'wrap'
    },
    searchBox: {
      flex: 1,
      minWidth: '250px',
      position: 'relative'
    },
    searchIcon: {
      position: 'absolute',
      left: '1rem',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#9ca3af',
      pointerEvents: 'none'
    },
    searchInput: {
      width: '100%',
      padding: '0.75rem 1rem 0.75rem 2.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      outline: 'none',
      transition: 'all 0.3s'
    },
    filterSelect: {
      padding: '0.75rem 1.5rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      background: 'white',
      cursor: 'pointer',
      outline: 'none',
      transition: 'all 0.3s'
    },
    facultyGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
      gap: '1.5rem'
    },
    facultyCard: {
      background: 'white',
      borderRadius: '1rem',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      transition: 'all 0.3s',
      cursor: 'pointer',
      position: 'relative'
    },
    deleteButton: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: '#ef4444',
      color: 'white',
      border: 'none',
      borderRadius: '50%',
      width: '2.5rem',
      height: '2.5rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
      transition: 'all 0.3s',
      zIndex: 10
    },
    cardHeader: {
      background: 'linear-gradient(90deg, #3b82f6 0%, #a855f7 100%)',
      height: '8rem',
      position: 'relative'
    },
    profileImage: {
      width: '6rem',
      height: '6rem',
      borderRadius: '50%',
      border: '4px solid white',
      position: 'absolute',
      bottom: '-3rem',
      left: '1.5rem',
      objectFit: 'cover'
    },
    cardBody: {
      padding: '4rem 1.5rem 1.5rem'
    },
    facultyName: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '0.25rem'
    },
    facultyPosition: {
      color: '#2563eb',
      fontWeight: '600',
      marginBottom: '0.25rem'
    },
    facultyDepartment: {
      color: '#6b7280',
      fontSize: '0.875rem',
      marginBottom: '1rem'
    },
    detailRow: {
      display: 'flex',
      gap: '0.5rem',
      marginBottom: '0.75rem',
      alignItems: 'flex-start'
    },
    detailText: {
      fontSize: '0.875rem',
      color: '#374151'
    },
    exchangeProgram: {
      fontWeight: '600',
      color: '#1f2937',
      fontSize: '0.875rem'
    },
    exchangeDuration: {
      fontSize: '0.75rem',
      color: '#6b7280'
    },
    statsContainer: {
      display: 'flex',
      gap: '1rem',
      padding: '1rem 0',
      margin: '1rem 0',
      borderTop: '1px solid #e5e7eb'
    },
    statBox: {
      flex: 1,
      textAlign: 'center'
    },
    statNumber: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#2563eb'
    },
    statLabel: {
      fontSize: '0.75rem',
      color: '#6b7280',
      marginTop: '0.25rem'
    },
    contactInfo: {
      marginTop: '1rem'
    },
    contactLink: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      color: '#6b7280',
      textDecoration: 'none',
      fontSize: '0.875rem',
      marginBottom: '0.5rem',
      transition: 'color 0.3s'
    },
    viewProfileBtn: {
      width: '100%',
      padding: '0.5rem',
      marginTop: '1rem',
      background: 'linear-gradient(90deg, #2563eb 0%, #9333ea 100%)',
      color: 'white',
      border: 'none',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s'
    },
    noResults: {
      textAlign: 'center',
      padding: '3rem',
      color: '#6b7280',
      fontSize: '1.125rem'
    },
    modalOverlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '1rem'
    },
    modalContent: {
      background: 'white',
      borderRadius: '1rem',
      padding: '2rem',
      maxWidth: '600px',
      width: '100%',
      maxHeight: '90vh',
      overflowY: 'auto',
      position: 'relative'
    },
    closeButton: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      background: 'none',
      border: 'none',
      fontSize: '1.5rem',
      cursor: 'pointer',
      color: '#6b7280',
      width: '2rem',
      height: '2rem',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      transition: 'all 0.3s'
    },
    formTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      marginBottom: '1.5rem',
      color: '#1f2937'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '1rem'
    },
    formGroup: {
      marginBottom: '1rem'
    },
    fullWidth: {
      gridColumn: '1 / -1'
    },
    label: {
      display: 'block',
      marginBottom: '0.5rem',
      fontWeight: '600',
      color: '#374151'
    },
    input: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      outline: 'none',
      transition: 'all 0.3s'
    },
    textarea: {
      width: '100%',
      padding: '0.75rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      outline: 'none',
      transition: 'all 0.3s',
      resize: 'vertical',
      minHeight: '80px'
    },
    imageUpload: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
      marginBottom: '1rem'
    },
    imagePreview: {
      width: '80px',
      height: '80px',
      borderRadius: '50%',
      objectFit: 'cover',
      border: '2px solid #e5e7eb'
    },
    uploadButton: {
      padding: '0.5rem 1rem',
      background: '#f3f4f6',
      border: '1px dashed #d1d5db',
      borderRadius: '0.5rem',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s'
    },
    submitButton: {
      background: 'linear-gradient(90deg, #2563eb 0%, #9333ea 100%)',
      color: 'white',
      border: 'none',
      padding: '0.75rem 2rem',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s',
      marginTop: '1rem'
    },
    buttonGroup: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'flex-end',
      marginTop: '1.5rem'
    },
    deleteButtonForm: {
      background: '#ef4444',
      color: 'white',
      border: 'none',
      padding: '0.75rem 1.5rem',
      borderRadius: '0.5rem',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s'
    }
  };

  const [facultyData, setFacultyData] = useState([
    {
      id: 1,
      name: "Dr. Sarah Chen",
      department: "Computer Science",
      position: "Professor",
      specialization: "Artificial Intelligence & Machine Learning",
      email: "sarah.chen@university.edu",
      phone: "+1 (555) 123-4567",
      image: "https://images.unsplash.com/photo-1573496359142-b8d87734a5a2?w=400&h=400&fit=crop",
      exchangeProgram: "MIT Exchange Program",
      duration: "Fall 2025",
      publications: 45,
      experience: "15 years"
    },
    {
      id: 2,
      name: "Prof. James Rodriguez",
      department: "Mechanical Engineering",
      position: "Associate Professor",
      specialization: "Robotics & Automation",
      email: "j.rodriguez@university.edu",
      phone: "+1 (555) 234-5678",
      image: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=400&fit=crop",
      exchangeProgram: "Stanford Partnership",
      duration: "Spring 2026",
      publications: 32,
      experience: "12 years"
    },
    {
      id: 3,
      name: "Dr. Priya Sharma",
      department: "Biotechnology",
      position: "Senior Lecturer",
      specialization: "Genetic Engineering",
      email: "priya.sharma@university.edu",
      phone: "+1 (555) 345-6789",
      image: "https://images.unsplash.com/photo-1580489944761-15a19d654956?w=400&h=400&fit=crop",
      exchangeProgram: "Oxford Research Initiative",
      duration: "Summer 2025",
      publications: 28,
      experience: "10 years"
    }
  ]);

  const departments = ['All', ...new Set(facultyData.map(f => f.department))];

  const filteredFaculty = facultyData.filter(faculty => {
    const matchesSearch = faculty.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         faculty.specialization.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         faculty.department.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = selectedDepartment === 'All' || faculty.department === selectedDepartment;
    return matchesSearch && matchesDepartment;
  });

  const handleAddTeacher = () => {
    const newTeacherData = {
      ...newTeacher,
      id: Date.now(),
      publications: parseInt(newTeacher.publications) || 0
    };
    setFacultyData([...facultyData, newTeacherData]);
    setNewTeacher({
      name: '',
      department: '',
      position: '',
      specialization: '',
      email: '',
      phone: '',
      image: '',
      exchangeProgram: '',
      duration: '',
      publications: '',
      experience: ''
    });
    setIsAddFormOpen(false);
  };

  const handleEditTeacher = () => {
    setFacultyData(facultyData.map(teacher => 
      teacher.id === editingTeacher.id ? editingTeacher : teacher
    ));
    setIsEditFormOpen(false);
    setEditingTeacher(null);
  };

  const handleDeleteTeacher = (id) => {
    setFacultyData(facultyData.filter(teacher => teacher.id !== id));
  };

  const handleEditClick = (teacher) => {
    setEditingTeacher({...teacher});
    setIsEditFormOpen(true);
  };

  const handleImageUpload = (event, isEdit = false) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (isEdit) {
          setEditingTeacher({...editingTeacher, image: e.target.result});
        } else {
          setNewTeacher({...newTeacher, image: e.target.result});
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const TeacherForm = ({ isEdit, teacher, onSubmit, onCancel }) => (
    <div style={styles.modalOverlay} onClick={onCancel}>
      <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <button style={styles.closeButton} onClick={onCancel}>
          <X size={20} />
        </button>

        <h2 style={styles.formTitle}>
          {isEdit ? 'Edit Teacher Profile' : 'Add New Teacher'}
        </h2>

        <div style={styles.imageUpload}>
          <img 
            src={teacher.image || "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=400&fit=crop"} 
            alt="Preview" 
            style={styles.imagePreview}
          />
          <label style={styles.uploadButton}>
            <Camera size={16} />
            Upload Photo
            <input
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={(e) => handleImageUpload(e, isEdit)}
            />
          </label>
        </div>

        <div style={styles.formGrid}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Full Name</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.name}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter full name"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Department</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.department}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter department"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Position</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.position}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter position"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Specialization</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.specialization}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter specialization"
            />
          </div>

          <div style={{...styles.formGroup, ...styles.fullWidth}}>
            <label style={styles.label}>Email</label>
            <input
              style={styles.input}
              type="email"
              value={teacher.email}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter email address"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Phone</label>
            <input
              style={styles.input}
              type="tel"
              value={teacher.phone}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter phone number"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Publications</label>
            <input
              style={styles.input}
              type="number"
              value={teacher.publications}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Number of publications"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Experience</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.experience}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="e.g., 15 years"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Exchange Program</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.exchangeProgram}
              onChange={(e) => isEdit
  ? setEditingTeacher(prev => ({ ...prev, name: e.target.value }))
  : setNewTeacher(prev => ({ ...prev, name: e.target.value }))
}

              placeholder="Enter exchange program"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Duration</label>
            <input
              style={styles.input}
              type="text"
              value={teacher.duration}
              onChange={(e) => isEdit ?
                setEditingTeacher({...teacher, duration: e.target.value}) :
                setNewTeacher({...newTeacher, duration: e.target.value})
              }
              placeholder="e.g., Fall 2025"
            />
          </div>
        </div>

        <div style={styles.buttonGroup}>
          {isEdit && (
            <button 
              style={styles.deleteButtonForm}
              onClick={() => {
                handleDeleteTeacher(teacher.id);
                onCancel();
              }}
            >
              Delete Teacher
            </button>
          )}
          <button 
            style={styles.submitButton}
            onClick={onSubmit}
            onMouseEnter={(e) => {
              e.currentTarget.style.background = 'linear-gradient(90deg, #1d4ed8 0%, #7e22ce 100%)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.background = 'linear-gradient(90deg, #2563eb 0%, #9333ea 100%)';
            }}
          >
            {isEdit ? 'Update Profile' : 'Add Teacher'}
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <button 
          style={styles.addButton}
          onClick={() => setIsAddFormOpen(true)}
          onMouseEnter={(e) => {
            e.currentTarget.style.background = 'rgba(255, 255, 255, 0.3)';
            e.currentTarget.style.transform = 'translateY(-2px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.background = 'rgba(255, 255, 255, 0.2)';
            e.currentTarget.style.transform = 'translateY(0)';
          }}
        >
          <Plus size={20} />
          Add Teacher
        </button>
        <h1 style={styles.headerTitle}>Faculty Exchange Program</h1>
        <p style={styles.headerSubtitle}>Connecting excellence through international collaboration</p>
      </div>

      <div style={styles.contentContainer}>
        <div style={styles.searchSection}>
          <div style={styles.searchControls}>
            <div style={styles.searchBox}>
              <Search style={styles.searchIcon} size={20} />
              <input
                type="text"
                placeholder="Search by name, specialization, or department..."
                style={styles.searchInput}
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>
            <select
              style={styles.filterSelect}
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
              onFocus={(e) => e.target.style.borderColor = '#2563eb'}
              onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
            >
              {departments.map(dept => (
                <option key={dept} value={dept}>{dept}</option>
              ))}
            </select>
          </div>
        </div>

        <div style={styles.facultyGrid}>
          {filteredFaculty.map(faculty => (
            <div 
              key={faculty.id} 
              style={styles.facultyCard}
              onMouseEnter={(e) => {
                e.currentTarget.style.boxShadow = '0 20px 25px -5px rgba(0, 0, 0, 0.1)';
                e.currentTarget.style.transform = 'translateY(-4px)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.boxShadow = '0 4px 6px -1px rgba(0, 0, 0, 0.1)';
                e.currentTarget.style.transform = 'translateY(0)';
              }}
            >
              <button 
                style={styles.deleteButton}
                onClick={() => handleDeleteTeacher(faculty.id)}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = '#dc2626';
                  e.currentTarget.style.transform = 'scale(1.1)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = '#ef4444';
                  e.currentTarget.style.transform = 'scale(1)';
                }}
              >
                <X size={16} />
              </button>

              <div style={styles.cardHeader}>
                <img
                  src={faculty.image}
                  alt={faculty.name}
                  style={styles.profileImage}
                />
              </div>

              <div style={styles.cardBody}>
                <h3 style={styles.facultyName}>{faculty.name}</h3>
                <p style={styles.facultyPosition}>{faculty.position}</p>
                <p style={styles.facultyDepartment}>{faculty.department}</p>

                <div style={styles.detailRow}>
                  <BookOpen color="#9333ea" size={18} style={{ marginTop: '2px', flexShrink: 0 }} />
                  <p style={styles.detailText}>{faculty.specialization}</p>
                </div>

                <div style={styles.detailRow}>
                  <Globe color="#3b82f6" size={18} style={{ marginTop: '2px', flexShrink: 0 }} />
                  <div>
                    <p style={styles.exchangeProgram}>{faculty.exchangeProgram}</p>
                    <p style={styles.exchangeDuration}>{faculty.duration}</p>
                  </div>
                </div>

                <div style={styles.statsContainer}>
                  <div style={styles.statBox}>
                    <div style={styles.statNumber}>{faculty.publications}</div>
                    <div style={styles.statLabel}>Publications</div>
                  </div>
                  <div style={{...styles.statBox, borderLeft: '1px solid #e5e7eb'}}>
                    <div style={{...styles.statNumber, color: '#9333ea'}}>{faculty.experience}</div>
                    <div style={styles.statLabel}>Experience</div>
                  </div>
                </div>

                <div style={styles.contactInfo}>
                  <a 
                    href={`mailto:${faculty.email}`} 
                    style={styles.contactLink}
                    onMouseEnter={(e) => e.currentTarget.style.color = '#2563eb'}
                    onMouseLeave={(e) => e.currentTarget.style.color = '#6b7280'}
                  >
                    <Mail size={16} />
                    <span style={{overflow: 'hidden', textOverflow: 'ellipsis'}}>{faculty.email}</span>
                  </a>
                  <a 
                    href={`tel:${faculty.phone}`} 
                    style={styles.contactLink}
                    onMouseEnter={(e) => e.currentTarget.style.color = '#2563eb'}
                    onMouseLeave={(e) => e.currentTarget.style.color = '#6b7280'}
                  >
                    <Phone size={16} />
                    <span>{faculty.phone}</span>
                  </a>
                </div>

                <button 
                  style={styles.viewProfileBtn}
                  onClick={() => handleEditClick(faculty)}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.background = 'linear-gradient(90deg, #1d4ed8 0%, #7e22ce 100%)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.background = 'linear-gradient(90deg, #2563eb 0%, #9333ea 100%)';
                  }}
                >
                  Edit Profile
                </button>
              </div>
            </div>
          ))}
        </div>

        {filteredFaculty.length === 0 && (
          <div style={styles.noResults}>
            No faculty members found matching your criteria.
          </div>
        )}
      </div>

      {isAddFormOpen && (
        <TeacherForm
          isEdit={false}
          teacher={newTeacher}
          onSubmit={handleAddTeacher}
          onCancel={() => setIsAddFormOpen(false)}
        />
      )}

      {isEditFormOpen && editingTeacher && (
        <TeacherForm
          isEdit={true}
          teacher={editingTeacher}
          onSubmit={handleEditTeacher}
          onCancel={() => {
            setIsEditFormOpen(false);
            setEditingTeacher(null);
          }}
        />
      )}
    </div>
  );
};

export default FacultyExchange;
