import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import image from "./image.png";
import { initializeApp } from "firebase/app";
import {
  getDatabase,
  ref,
  set,
  get,
  child
} from "firebase/database";
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification
} from "firebase/auth";


const firebaseConfig = {
  apiKey: "AIzaSyC6QZ-ER8zZXQFUia5AVEB9P0zG8WlagJA",
  authDomain: "inti-5b9a3.firebaseapp.com",
  projectId: "inti-5b9a3",
  storageBucket: "inti-5b9a3.firebasestorage.app",
  messagingSenderId: "762204179355",
  appId: "1:762204179355:web:86d6bb10a40672b136ef3e",
  measurementId: "G-XTBB1611XH",
  databaseURL: "https://inti-5b9a3-default-rtdb.firebaseio.com",
};


const app = initializeApp(firebaseConfig);
const db = getDatabase(app);
const auth = getAuth(app);

const INTILoginPage = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({ email: "", username: "", password: "" });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };


  const handleSubmit = async () => {
    if (isSignUp) {
      try {

        const userCredential = await createUserWithEmailAndPassword(
          auth,
          formData.email,
          formData.password
        );
        const user = userCredential.user;


        await sendEmailVerification(user);


        await set(ref(db, "users/" + user.uid), {
          email: formData.email,
          username: formData.username,
          uid: user.uid,
          emailVerified: false,
        });

        alert("âœ… Verification email sent! Please verify before logging in.");
        setIsSignUp(false);
        setFormData({ email: "", username: "", password: "" });
      } catch (error) {
        alert(error.message);
      }
    } else {
      try {
        const userCredential = await signInWithEmailAndPassword(
          auth,
          formData.email,
          formData.password
        );
        const user = userCredential.user;

        if (!user.emailVerified) {
          alert("âš ï¸ Please verify your email before logging in.");
          return;
        }

        const dbRef = ref(db);
        const snapshot = await get(child(dbRef, `users/${user.uid}`));

        if (snapshot.exists()) {
          alert(`Welcome back, ${snapshot.val().username}!`);
          onLogin();
          navigate("/");
        } else {
          alert("User data not found. Please sign up first.");
        }
      } catch (error) {
        alert(error.message);
      }
    }
  };

  // ðŸ”„ Toggle form
  const toggleForm = () => {
    setIsSignUp(!isSignUp);
    setFormData({ email: "", username: "", password: "" });
  };

  // ðŸŽ¨ Styling (unchanged)
  const containerStyle = {
    minHeight: "100vh",
    position: "relative",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "1rem",
    backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${image})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };

  const logoContainerStyle = {
    position: "absolute",
    top: "2rem",
    left: "2rem",
    backgroundColor: "white",
    padding: "0.75rem 1rem",
    borderRadius: "0.5rem",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)",
    zIndex: 10,
  };

  const logoContentStyle = {
    display: "flex",
    alignItems: "center",
    gap: "0.75rem",
  };

  const logoIconStyle = { color: "#dc2626", fontSize: "2rem", fontWeight: "bold" };
  const logoTextStyle = { display: "flex", flexDirection: "column" };
  const logoTitleStyle = { fontSize: "1.5rem", fontWeight: "bold", color: "#1f2937" };
  const logoSubtitleStyle = { fontSize: "0.75rem", color: "#4b5563" };
  const formCardStyle = {
    backgroundColor: "white",
    borderRadius: "1rem",
    boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.3)",
    width: "100%",
    maxWidth: "28rem",
    padding: "2rem",
    zIndex: 1,
  };
  const formTitleStyle = {
    fontSize: "1.875rem",
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: "0.5rem",
    color: "#1f2937",
  };
  const formSubtitleStyle = {
    textAlign: "center",
    color: "#4b5563",
    marginBottom: "2rem",
  };
  const formFieldsStyle = { display: "flex", flexDirection: "column", gap: "1.25rem" };
  const inputGroupStyle = { display: "flex", flexDirection: "column" };
  const inputLabelStyle = {
    fontSize: "0.875rem",
    fontWeight: "500",
    color: "#374151",
    marginBottom: "0.5rem",
  };
  const inputFieldStyle = {
    width: "100%",
    padding: "0.75rem 1rem",
    border: "1px solid #d1d5db",
    borderRadius: "0.5rem",
    outline: "none",
    fontSize: "1rem",
  };
  const submitButtonStyle = {
    width: "100%",
    backgroundColor: "#dc2626",
    color: "white",
    padding: "0.75rem",
    borderRadius: "0.5rem",
    fontWeight: "600",
    border: "none",
    cursor: "pointer",
    fontSize: "1rem",
    transition: "all 0.2s",
  };

  const toggleButtonStyle = {
    color: "#dc2626",
    fontWeight: "600",
    background: "none",
    border: "none",
    cursor: "pointer",
    fontSize: "1rem",
  };

  return (
    <div style={containerStyle}>
      <div style={logoContainerStyle}>
        <div style={logoContentStyle}>
          <div style={logoIconStyle}>âœ±</div>
          <div style={logoTextStyle}>
            <span style={logoTitleStyle}>INTI</span>
            <span style={logoSubtitleStyle}>
              International University & Colleges
            </span>
          </div>
        </div>
      </div>

      <div style={formCardStyle}>
        <h2 style={formTitleStyle}>
          {isSignUp ? "Create Account" : "Welcome Back"}
        </h2>
        <p style={formSubtitleStyle}>
          {isSignUp ? "Sign up to get started" : "Login to your account"}
        </p>

        <div style={formFieldsStyle}>
          <div style={inputGroupStyle}>
            <label style={inputLabelStyle}>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              style={inputFieldStyle}
              placeholder="Enter your email"
            />
          </div>

          {isSignUp && (
            <div style={inputGroupStyle}>
              <label style={inputLabelStyle}>Username</label>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                style={inputFieldStyle}
                placeholder="Enter your username"
              />
            </div>
          )}

          <div style={inputGroupStyle}>
            <label style={inputLabelStyle}>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              style={inputFieldStyle}
              placeholder="Enter your password"
            />
          </div>

          <button
            onClick={handleSubmit}
            style={submitButtonStyle}
          >
            {isSignUp ? "Sign Up" : "Login"}
          </button>
        </div>

        <div style={{ textAlign: "center", marginTop: 20 }}>
          <p>
            {isSignUp ? "Already have an account?" : "Don't have an account?"}{" "}
            <button onClick={toggleForm} style={toggleButtonStyle}>
              {isSignUp ? "Login" : "Sign Up"}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default INTILoginPage;
