import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import image from "./image.png";
import { initializeApp } from "firebase/app";
import {
  getDatabase,
  ref,
  set,
  push,
  get,
  remove,
  child
} from "firebase/database";
import {
  getAuth,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";

// ðŸ”¥ Firebase Config
const firebaseConfig = {
  apiKey: "AIzaSyC6QZ-ER8zZXQFUia5AVEB9P0zG8WlagJA",
  authDomain: "inti-5b9a3.firebaseapp.com",
  projectId: "inti-5b9a3",
  storageBucket: "inti-5b9a3.firebasestorage.app",
  messagingSenderId: "762204179355",
  appId: "1:762204179355:web:86d6bb10a40672b136ef3e",
  measurementId: "G-XTBB1611XH",
  databaseURL: "https://inti-5b9a3-default-rtdb.firebaseio.com",
};

// ðŸ”§ Firebase Init
const app = initializeApp(firebaseConfig);
const db = getDatabase(app);
const auth = getAuth(app);

const INTILoginPage = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    username: "",
    password: "",
  });
  const [status, setStatus] = useState("");

  // ðŸ§© Detect if the user clicked a verification link
  useEffect(() => {
    const verifyLink = async () => {
      if (isSignInWithEmailLink(auth, window.location.href)) {
        let email = window.localStorage.getItem("emailForSignIn");
        if (!email) {
          email = window.prompt("Please provide your email for confirmation");
        }

        try {
          await signInWithEmailLink(auth, email, window.location.href);

          // Find pending user data
          const pendingRef = ref(db, "pendingUsers");
          const snapshot = await get(pendingRef);
          let foundUser = null;
          let pendingKey = null;

          if (snapshot.exists()) {
            Object.entries(snapshot.val()).forEach(([key, value]) => {
              if (value.email === email) {
                foundUser = value;
                pendingKey = key;
              }
            });
          }

          if (foundUser) {
            // Create real account
            const userCredential = await createUserWithEmailAndPassword(
              auth,
              foundUser.email,
              foundUser.password
            );
            const user = userCredential.user;

            await set(ref(db, "users/" + user.uid), {
              email: foundUser.email,
              username: foundUser.username,
              uid: user.uid,
              emailVerified: true,
            });

            await remove(ref(db, `pendingUsers/${pendingKey}`));

            setStatus("âœ… Email verified! Account successfully created.");
            alert("Email verified successfully! You can now log in.");
            navigate("/");
          } else {
            setStatus("âŒ No pending signup found for this email.");
          }
        } catch (error) {
          console.error(error);
          setStatus("Verification failed: " + error.message);
        }
      }
    };

    verifyLink();
  }, [navigate]);

  // ðŸ–Šï¸ Input Change
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // ðŸš€ Handle Signup / Login
  const handleSubmit = async () => {
    if (isSignUp) {
      try {
        if (!formData.email || !formData.username || !formData.password) {
          alert("Please fill all fields");
          return;
        }

        // Save pending user
        const newRef = push(ref(db, "pendingUsers"));
        await set(newRef, {
          email: formData.email,
          username: formData.username,
          password: formData.password,
          verified: false,
        });

        // Send email link
        const actionCodeSettings = {
          url: window.location.origin, // same page handles verification
          handleCodeInApp: true,
        };

        await sendSignInLinkToEmail(auth, formData.email, actionCodeSettings);
        window.localStorage.setItem("emailForSignIn", formData.email);
        alert("âœ… Verification link sent! Please check your email.");
      } catch (error) {
        alert(error.message);
      }
    } else {
      // ðŸ”µ LOGIN
      try {
        const userCredential = await signInWithEmailAndPassword(
          auth,
          formData.email,
          formData.password
        );
        const user = userCredential.user;

        const dbRef = ref(db);
        const snapshot = await get(child(dbRef, `users/${user.uid}`));
        if (snapshot.exists()) {
          alert(`Welcome back, ${snapshot.val().username}!`);
          onLogin();
          navigate("/");
        } else {
          alert("No verified account found. Please verify your email first.");
        }
      } catch (error) {
        alert(error.message);
      }
    }
  };

  // Toggle form mode
  const toggleForm = () => {
    setIsSignUp(!isSignUp);
    setFormData({ email: "", username: "", password: "" });
  };

  // ðŸŽ¨ Styling (unchanged)
  const containerStyle = {
    minHeight: "100vh",
    position: "relative",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "1rem",
    backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${image})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };

  const logoContainerStyle = {
    position: "absolute",
    top: "2rem",
    left: "2rem",
    backgroundColor: "white",
    padding: "0.75rem 1rem",
    borderRadius: "0.5rem",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)",
    zIndex: 10,
  };

  const logoContentStyle = {
    display: "flex",
    alignItems: "center",
    gap: "0.75rem",
  };

  const logoIconStyle = { color: "#dc2626", fontSize: "2rem", fontWeight: "bold" };
  const logoTextStyle = { display: "flex", flexDirection: "column" };
  const logoTitleStyle = { fontSize: "1.5rem", fontWeight: "bold", color: "#1f2937" };
  const logoSubtitleStyle = { fontSize: "0.75rem", color: "#4b5563" };
  const formCardStyle = {
    backgroundColor: "white",
    borderRadius: "1rem",
    boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.3)",
    width: "100%",
    maxWidth: "28rem",
    padding: "2rem",
    zIndex: 1,
  };
  const formTitleStyle = {
    fontSize: "1.875rem",
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: "0.5rem",
    color: "#1f2937",
  };
  const formSubtitleStyle = {
    textAlign: "center",
    color: "#4b5563",
    marginBottom: "2rem",
  };
  const formFieldsStyle = { display: "flex", flexDirection: "column", gap: "1.25rem" };
  const inputGroupStyle = { display: "flex", flexDirection: "column" };
  const inputLabelStyle = {
    fontSize: "0.875rem",
    fontWeight: "500",
    color: "#374151",
    marginBottom: "0.5rem",
  };
  const inputFieldStyle = {
    width: "100%",
    padding: "0.75rem 1rem",
    border: "1px solid #d1d5db",
    borderRadius: "0.5rem",
    outline: "none",
    fontSize: "1rem",
    transition: "all 0.2s",
  };
  const submitButtonStyle = {
    width: "100%",
    backgroundColor: "#dc2626",
    color: "white",
    padding: "0.75rem",
    borderRadius: "0.5rem",
    fontWeight: "600",
    border: "none",
    cursor: "pointer",
    fontSize: "1rem",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "all 0.2s",
  };
  const toggleFormStyle = { marginTop: "1.5rem", textAlign: "center" };
  const toggleTextStyle = { color: "#4b5563" };
  const toggleButtonStyle = {
    color: "#dc2626",
    fontWeight: "600",
    background: "none",
    border: "none",
    cursor: "pointer",
    textDecoration: "none",
    fontSize: "1rem",
  };

  return (
    <div style={containerStyle}>
      <div style={logoContainerStyle}>
        <div style={logoContentStyle}>
          <div style={logoIconStyle}>âœ±</div>
          <div style={logoTextStyle}>
            <span style={logoTitleStyle}>INTI</span>
            <span style={logoSubtitleStyle}>International University & Colleges</span>
          </div>
        </div>
      </div>

      <div style={formCardStyle}>
        <h2 style={formTitleStyle}>{isSignUp ? "Create Account" : "Welcome Back"}</h2>
        <p style={formSubtitleStyle}>
          {isSignUp ? "Sign up to get started" : "Login to your account"}
        </p>

        <div style={formFieldsStyle}>
          <div style={inputGroupStyle}>
            <label style={inputLabelStyle}>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              style={inputFieldStyle}
              placeholder="Enter your email"
            />
          </div>

          {isSignUp && (
            <div style={inputGroupStyle}>
              <label style={inputLabelStyle}>Username</label>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                style={inputFieldStyle}
                placeholder="Enter your username"
              />
            </div>
          )}

          <div style={inputGroupStyle}>
            <label style={inputLabelStyle}>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              style={inputFieldStyle}
              placeholder="Enter your password"
            />
          </div>

          <button
            onClick={handleSubmit}
            style={submitButtonStyle}
            onMouseEnter={(e) => (e.target.style.backgroundColor = "#b91c1c")}
            onMouseLeave={(e) => (e.target.style.backgroundColor = "#dc2626")}
          >
            {isSignUp ? "Send Verification Link" : "Login"}
          </button>
        </div>

        {status && (
          <p style={{ textAlign: "center", color: "green", marginTop: 10 }}>{status}</p>
        )}

        <div style={toggleFormStyle}>
          <p style={toggleTextStyle}>
            {isSignUp ? "Already have an account?" : "Don't have an account?"}{" "}
            <button
              onClick={toggleForm}
              style={toggleButtonStyle}
              onMouseEnter={(e) => (e.target.style.textDecoration = "underline")}
              onMouseLeave={(e) => (e.target.style.textDecoration = "none")}
            >
              {isSignUp ? "Login" : "Sign Up"}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default INTILoginPage;
