import React, { useState } from 'react';

const AboutHeroSection = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <section className="about-hero-section">
            <div className="container">
                {/* Top Section */}
                <h1 className="about-hero-title">
                    INTI International University
                </h1>
                <p className="about-hero-description">
                    INTI International University, Nilai serves as the flagship campus that offers industry relevant programmes 
                    across various disciplines along with a residential campus experience.
                </p>

                {/* Image Section */}
                <div className="ranking-banner-container">
                    <div className="ranking-banner-placeholder">
                        Ranking Banner Image
                    </div>
                </div>

                {/* Menu Icon */}
                <div className="menu-icon" onClick={toggleMenu}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>

                {/* Slide Menu */}
                <div className={`side-menu ${isMenuOpen ? 'open' : ''}`}>
                    <ul>
                        <li>Home</li>
                        <li>Faculty Exchange</li>
                        <li>Study Tour</li>
                        <li>MOU/S</li>
                        <li>Dual Degree Program</li>
                        <li>Franchise Program</li>
                        <li>Mobility</li>
                        <li>Global Partners</li>
                    </ul>
                </div>

                {/* Overlay */}
                {isMenuOpen && <div className="overlay" onClick={toggleMenu}></div>}
            </div>

            {/* Inline CSS */}
            <style>{`
                .about-hero-section {
                    position: relative;
                    padding: 80px 20px;
                    background: #f5f5f5;
                    font-family: 'Poppins', sans-serif;
                    overflow-x: hidden;
                }

                .container {
                    max-width: 900px;
                    margin: 0 auto;
                    text-align: center;
                }

                .about-hero-title {
                    font-size: 2.5rem;
                    font-weight: 700;
                    margin-bottom: 20px;
                    color: #222;
                }

                .about-hero-description {
                    font-size: 1.1rem;
                    color: #555;
                    margin-bottom: 30px;
                    line-height: 1.6;
                }

                .ranking-banner-container {
                    margin-top: 30px;
                }

                .ranking-banner-placeholder {
                    width: 100%;
                    height: 300px;
                    background: #ddd;
                    border-radius: 10px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: #888;
                    font-size: 1.2rem;
                    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
                }

                /* Menu Icon (three lines) */
                .menu-icon {
                    position: fixed;
                    top: 25px;
                    left: 25px;
                    width: 35px;
                    height: 30px;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    cursor: pointer;
                    z-index: 1001;
                }

                .menu-icon div {
                    height: 4px;
                    background: #333;
                    border-radius: 2px;
                    transition: all 0.3s ease;
                }

                /* Overlay */
                .overlay {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background: rgba(0, 0, 0, 0.5);
                    z-index: 999;
                    animation: fadeIn 0.3s ease;
                }

                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                /* Side Menu */
                .side-menu {
                    position: fixed;
                    top: 0;
                    left: -300px;
                    width: 300px;
                    height: 100%;
                    background: white;
                    box-shadow: 4px 0 20px rgba(0,0,0,0.15);
                    padding-top: 80px;
                    transition: left 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
                    z-index: 1000;
                    overflow-y: auto;
                }

                .side-menu.open {
                    left: 0;
                }

                .side-menu::before {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    height: 80px;
                    background: linear-gradient(to bottom, #f8f9fa, white);
                    border-bottom: 1px solid #e9ecef;
                }

                .side-menu ul {
                    list-style: none;
                    padding: 20px 0;
                    margin: 0;
                    text-align: left;
                }

                .side-menu li {
                    padding: 18px 30px;
                    font-size: 1.05rem;
                    font-weight: 500;
                    color: #333;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    border-left: 4px solid transparent;
                    position: relative;
                }

                .side-menu li::before {
                    content: '›';
                    position: absolute;
                    left: 15px;
                    opacity: 0;
                    transition: all 0.3s ease;
                    font-size: 1.3rem;
                    font-weight: bold;
                    color: #667eea;
                }

                .side-menu li:hover {
                    background: #f8f9fa;
                    border-left-color: #667eea;
                    padding-left: 35px;
                    transform: translateX(5px);
                }

                .side-menu li:hover::before {
                    opacity: 1;
                    left: 18px;
                }

                .side-menu li:active {
                    background: #e9ecef;
                }

                @media (max-width: 600px) {
                    .about-hero-title {
                        font-size: 2rem;
                    }
                    .menu-icon {
                        width: 30px;
                        height: 25px;
                    }
                    .side-menu {
                        width: 280px;
                        left: -280px;
                    }
                }
            `}</style>
        </section>
    );
};

export default AboutHeroSection;
