import React, { useState } from 'react';
import RankingBannerImage from '../Ranking-banner.jpg'; 

const AboutHeroSection = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <section className="about-hero-section">
            <div className="container">
                <h1 className="about-hero-title">
                    INTI International University
                </h1>
                <p className="about-hero-description">
                    INTI International University, Nilai serves as the flagship campus that offers industry relevant programmes 
                    across various disciplines along with a residential campus experience.
                </p>

                {/* Image Section */}
                <div className="ranking-banner-container">
                    <img 
                        src={RankingBannerImage} 
                        alt="INTI University Rankings Banner" 
                        className="ranking-banner-image" 
                    />
                </div>

                {/* Menu Icon */}
                <div className="menu-icon" onClick={toggleMenu}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>

                {/* Sidebar */}
                <div className={`side-menu ${isMenuOpen ? 'open' : ''}`}>
                    <ul>
                        <li>Home</li>
                        <li>Faculty Exchange</li>
                        <li>Study Tour</li>
                        <li>MOU/S</li>
                        <li>Mobility</li>
                        <li>Global Partners</li>
                    </ul>
                </div>
            </div>

            {/* ===== CSS for Sidebar Only ===== */}
            <style>{`
                /* MENU ICON (three lines) */
                .menu-icon {
                    position: fixed;
                    top: 25px;
                    left: 25px;
                    width: 35px;
                    height: 30px;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    cursor: pointer;
                    z-index: 1001;
                }

                .menu-icon div {
                    height: 4px;
                    background: #333;
                    border-radius: 2px;
                    transition: all 0.3s ease;
                }

                /* SLIDE SIDEBAR */
                .side-menu {
                    position: fixed;
                    top: 0;
                    left: -260px; /* hidden by default */
                    width: 260px;
                    height: 100%;
                    background: #fff;
                    box-shadow: 2px 0 10px rgba(0,0,0,0.1);
                    transition: left 0.4s ease;
                    z-index: 1000;
                    padding-top: 80px;
                }

                .side-menu.open {
                    left: 0;
                }

                .side-menu ul {
                    list-style: none;
                    margin: 0;
                    padding: 0;
                }

                .side-menu li {
                    padding: 15px 25px;
                    font-size: 1.1rem;
                    color: #333;
                    cursor: pointer;
                    transition: background 0.3s ease;
                }

                .side-menu li:hover {
                    background: #f5f5f5;
                }

                @media (max-width: 600px) {
                    .menu-icon {
                        width: 30px;
                        height: 25px;
                    }
                }
            `}</style>
        </section>
    );
};

export default AboutHeroSection;
