import React, { useState } from 'react';
import RankingBannerImage from '../Ranking-banner.jpg'; 

const AboutHeroSection = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const menuItems = [
        { icon: 'üè†', label: 'Home' },
        { icon: 'üîÑ', label: 'Faculty Exchange' },
        { icon: '‚úàÔ∏è', label: 'Study Tour' },
        { icon: 'üìã', label: 'MOU/S' },
        { icon: 'üåç', label: 'Mobility' },
        { icon: 'ü§ù', label: 'Global Partners' }
    ];

    return (
        <>
            <section className="about-hero-section">
                <div className="container">
                    <h1 className="about-hero-title">
                        INTI International University
                    </h1>
                    <p className="about-hero-description">
                        INTI International University, Nilai serves as the flagship campus that offers industry relevant programmes 
                        across various disciplines along with a residential campus experience.
                    </p>

                    {/* Image Section */}
                    <div className="ranking-banner-container">
                        <img 
                            src={RankingBannerImage} 
                            alt="INTI University Rankings Banner" 
                            className="ranking-banner-image" 
                        />
                    </div>

                    {/* Sidebar - Always visible on left */}
                    <div className="side-menu">
                        <ul>
                            {menuItems.map((item, index) => (
                                <li key={index}>
                                    <span className="menu-icon-svg">{item.icon}</span>
                                    <span className="menu-label">{item.label}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </section>

            <style>{`
                /* SIDEBAR - Always visible */
                .side-menu {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 200px;
                    height: 100%;
                    background: #fff;
                    box-shadow: 2px 0 10px rgba(0,0,0,0.1);
                    z-index: 1000;
                    padding-top: 40px;
                }

                .side-menu ul {
                    list-style: none;
                    margin: 0;
                    padding: 0;
                }

                .side-menu li {
                    padding: 18px 20px;
                    display: flex;
                    align-items: center;
                    gap: 15px;
                    font-size: 1rem;
                    color: #666;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    border-left: 4px solid transparent;
                }

                .side-menu li:hover {
                    background: #f8f9fa;
                    color: #333;
                }

                .side-menu li.active {
                    background: #f0f4ff;
                    color: #4154f1;
                    border-left-color: #4154f1;
                }

                .menu-icon-svg {
                    font-size: 1.5rem;
                    width: 24px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                .menu-label {
                    font-weight: 500;
                }

                /* Adjust main content to account for sidebar */
                .about-hero-section {
                    margin-left: 200px;
                }

                .about-hero-section .container {
                    padding: 40px;
                }

                @media (max-width: 768px) {
                    .side-menu {
                        width: 70px;
                    }

                    .menu-label {
                        display: none;
                    }

                    .about-hero-section {
                        margin-left: 70px;
                    }

                    .about-hero-section .container {
                        padding: 20px;
                    }
                }
            `}</style>
        </>
    );
};

export default AboutHeroSection;
