import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link } from 'react-router-dom';
import Header from './components/layout/Header.jsx';
import Footer from './components/layout/Footer.jsx';
import HeroSection from './components/sections/HeroSection.jsx';
import ReachSection from './components/sections/ReachSection.jsx';
import DiscoverPathSection from './components/sections/DiscoverPathSection.jsx';
import AboutHeroSection from './components/sections/AboutHeroSection.jsx'; 
import ScopusSection from './components/sections/ScopusSection.jsx'; 
import MOUListPage from './components/pages/MOUListPage.jsx'; 
import INTILoginPage from './components/pages/Login_Page.jsx';
import FacultyExchange from './components/sections/faculty_exchange.jsx';

// ‚úÖ Enhanced Sidebar with proper styling
const Sidebar = ({ activeRoute, setActiveRoute }) => (
  <aside className="fixed top-[64px] bottom-[64px] left-0 w-64 bg-white border-r border-gray-300 p-5 overflow-y-auto z-40 shadow-sm">
    <ul className="space-y-3 text-gray-800 font-medium">
      <li>
        <Link 
          to="/" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/')}
        >
          <span>üè†</span>
          <span>Home</span>
        </Link>
      </li>
      <li>
        <Link 
          to="/mou-partners" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/mou-partners' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/mou-partners')}
        >
          <span>ü§ù</span>
          <span>MOU Partners</span>
        </Link>
      </li>
      <li>
        <Link 
          to="/faculty-exchange" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/faculty-exchange' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/faculty-exchange')}
        >
          <span>üéì</span>
          <span>Faculty Exchange</span>
        </Link>
      </li>
      <li>
        <Link 
          to="/study-tour" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/study-tour' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/study-tour')}
        >
          <span>‚úàÔ∏è</span>
          <span>Study Tour</span>
        </Link>
      </li>
      <li>
        <Link 
          to="/mobility" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/mobility' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/mobility')}
        >
          <span>üåç</span>
          <span>Mobility</span>
        </Link>
      </li>
      <li>
        <Link 
          to="/global-partners" 
          className={`flex items-center gap-3 p-3 rounded-lg transition-all ${
            activeRoute === '/global-partners' ? 'bg-[#AE1C30] text-white' : 'hover:bg-gray-100 hover:text-[#AE1C30]'
          }`}
          onClick={() => setActiveRoute('/global-partners')}
        >
          <span>üåê</span>
          <span>Global Partners</span>
        </Link>
      </li>
    </ul>
  </aside>
);

// ‚úÖ Home Page Sections
const HomePageContent = () => (
  <div className="space-y-8">
    <HeroSection />
    <AboutHeroSection />
    <ReachSection />
    <ScopusSection /> 
    <DiscoverPathSection />
  </div>
);

// ‚úÖ Protected Route Component
const ProtectedRoute = ({ isAuthenticated, children }) => {
  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [activeRoute, setActiveRoute] = useState('/');

  const handleLogin = () => setIsAuthenticated(true);
  const handleLogout = () => setIsAuthenticated(false);

  return (
    <BrowserRouter>
      <div className="min-h-screen flex flex-col bg-white font-sans antialiased">

        {/* ‚úÖ Header (fixed top) */}
        {isAuthenticated && (
          <div className="fixed top-0 left-0 right-0 z-50 bg-white shadow-sm border-b border-gray-300">
            <Header onLogout={handleLogout} />
          </div>
        )}

        <div className="flex flex-1">
          {/* ‚úÖ Sidebar (always visible when authenticated) */}
          {isAuthenticated && (
            <Sidebar activeRoute={activeRoute} setActiveRoute={setActiveRoute} />
          )}

          {/* ‚úÖ Main Content Area with proper spacing */}
          <main className={`flex-1 min-h-screen transition-all duration-300 ${
            isAuthenticated ? 'ml-64' : 'ml-0'
          } ${isAuthenticated ? 'mt-16 mb-16' : 'mt-0 mb-0'}`}>
            <div className="p-6">
              <Routes>
                {/* Login Route */}
                <Route 
                  path="/login" 
                  element={
                    isAuthenticated ? (
                      <Navigate to="/" replace />
                    ) : (
                      <INTILoginPage onLogin={handleLogin} />
                    )
                  } 
                />

                {/* Protected Routes */}
                {isAuthenticated && (
                  <>
                    <Route 
                      path="/" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <HomePageContent />
                        </ProtectedRoute>
                      } 
                    />

                    <Route 
                      path="/mou-partners" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <MOUListPage />
                        </ProtectedRoute>
                      } 
                    />

                    <Route 
                      path="/faculty-exchange" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <FacultyExchange />
                        </ProtectedRoute>
                      } 
                    />

                    {/* Additional Routes for other sidebar items */}
                    <Route 
                      path="/study-tour" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <div className="p-6">
                            <h1 className="text-3xl font-bold text-gray-800 mb-4">Study Tour</h1>
                            <p className="text-gray-600">Study Tour content will be displayed here.</p>
                          </div>
                        </ProtectedRoute>
                      } 
                    />

                    <Route 
                      path="/mobility" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <div className="p-6">
                            <h1 className="text-3xl font-bold text-gray-800 mb-4">Mobility</h1>
                            <p className="text-gray-600">Mobility content will be displayed here.</p>
                          </div>
                        </ProtectedRoute>
                      } 
                    />

                    <Route 
                      path="/global-partners" 
                      element={
                        <ProtectedRoute isAuthenticated={isAuthenticated}>
                          <div className="p-6">
                            <h1 className="text-3xl font-bold text-gray-800 mb-4">Global Partners</h1>
                            <p className="text-gray-600">Global Partners content will be displayed here.</p>
                          </div>
                        </ProtectedRoute>
                      } 
                    />
                  </>
                )}

                {/* Fallback Route */}
                <Route path="*" element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />} />
              </Routes>
            </div>
          </main>
        </div>

        {/* ‚úÖ Footer (fixed bottom) */}
        {isAuthenticated && (
          <div className="fixed bottom-0 left-0 right-0 z-50 bg-white shadow-inner border-t border-gray-300">
            <Footer />
          </div>
        )}
      </div>
    </BrowserRouter>
  );
};

export default App;
