import React, { useState } from 'react';

const INTILoginPage = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    password: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = () => {
    if (isSignUp) {
      console.log('Sign up with:', formData);
      alert('Sign up successful!');
    } else {
      console.log('Login with:', { username: formData.username, password: formData.password });
      alert('Login successful!');
    }
  };

  const toggleForm = () => {
    setIsSignUp(!isSignUp);
    setFormData({ email: '', username: '', password: '' });
  };

  return (
    <div className="min-h-screen relative flex items-center justify-center p-4 bg-gradient-to-br from-red-50 via-white to-red-100">
      {/* Logo in top left corner */}
      <div className="absolute top-8 left-8 bg-white px-4 py-3 rounded-lg shadow-lg z-10">
        <div className="flex items-center gap-3">
          <div className="text-red-600 text-3xl font-bold">âœ±</div>
          <div className="flex flex-col">
            <span className="text-2xl font-bold text-gray-800">INTI</span>
            <span className="text-xs text-gray-600">International University & Colleges</span>
          </div>
        </div>
      </div>

      {/* Login/Signup Card */}
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md p-8 z-1">
        <h2 className="text-3xl font-bold text-center mb-2 text-gray-800">
          {isSignUp ? 'Create Account' : 'Welcome Back'}
        </h2>
        <p className="text-center text-gray-600 mb-8">
          {isSignUp ? 'Sign up to get started' : 'Login to your account'}
        </p>

        <div className="flex flex-col gap-5">
          {isSignUp && (
            <div className="flex flex-col">
              <label className="text-sm font-medium text-gray-700 mb-2">Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg outline-none text-base focus:border-red-600 focus:ring-2 focus:ring-red-200 transition-all"
                placeholder="Enter your email"
              />
            </div>
          )}

          <div className="flex flex-col">
            <label className="text-sm font-medium text-gray-700 mb-2">Username</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg outline-none text-base focus:border-red-600 focus:ring-2 focus:ring-red-200 transition-all"
              placeholder="Enter your username"
            />
          </div>

          <div className="flex flex-col">
            <label className="text-sm font-medium text-gray-700 mb-2">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg outline-none text-base focus:border-red-600 focus:ring-2 focus:ring-red-200 transition-all"
              placeholder="Enter your password"
            />
          </div>

          <button
            onClick={handleSubmit}
            className="w-full bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-semibold text-base shadow-md transition-all active:scale-95"
          >
            {isSignUp ? 'Sign Up' : 'Login'}
          </button>
        </div>

        <div className="mt-6 text-center">
          <p className="text-gray-600">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"}{' '}
            <button
              onClick={toggleForm}
              className="text-red-600 font-semibold hover:underline bg-transparent border-none cursor-pointer"
            >
              {isSignUp ? 'Login' : 'Sign Up'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default INTILoginPage;
